import {
    GET_ALL_POKEMONS,
    GET_POKEMON_DETAIL,
    // FILTER_POKEMONS,
    // ORDER_POKEMONS,
    // CREATE_POKEMON,
    // ADD_FAVORITE,
    // REMOVE_FAVORITE, 
    } from "../actions/index"


const initialState = {
    loadedPokemons: [],
    filteredPokemons: [],
    orderedPokemons: [],
    favoritesPokemons: [],
    pokemonDetail:{}
};

const reducer = (state= initialState, action)=>{
    switch(action.type){
        case GET_ALL_POKEMONS:
            return {
                ...state,
                loadedPokemons:action.payload.data
            }
        case GET_POKEMON_DETAIL:
            return {
                ...state,
                pokemonDetail:action.payload.data
            }    
        default:
            return state;
    }
}

export default reducer;
