import React, {useEffect, useState} from "react";
import { useSelector, useDispatch } from "react-redux";
import { filterByType, getAllPokemons } from "../../store/actions";


export default function Filter(){
    const pokemons = useSelector((state)=> state.loadedPokemons);  
    let dispatch = useDispatch();
    useEffect(() =>{
        dispatch(getAllPokemons())
    }, [dispatch]);

    const filter = useSelector((state)=> state.filteredPokemons)

    const [input, setInput] = useState('');

    const handleChange = (e) => {
        setInput(e.target.value);
        };


    const handleOnClick = (e) =>{
        dispatch(filterByType(input))
        setInput('');
        console.log(filter)
    }


    return <div>
            <input 
                value={input}
                onChange={handleChange}/>
            <button onClick = {handleOnClick}>Filtrar</button>
    </div>
}
