import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getPokemonByName } from '../store/actions';
import Pokemon from './Pokemon'


export default function Search (){  
    const [state, setState] = useState({
        input: ""
      });

    const handleChange = (e) => {
        setState({...state,[e.target.name]: e.target.value });
        };

    const dispatch = useDispatch();

    const handleSubmit = (e) =>{
        e.preventDefault();
        dispatch(getPokemonByName(state.input))
        setState({...state,[e.target.name]: '' });
    }
    const foundPokemon = useSelector((state)=> state.pokemonDetail)
    const { id, name, hp, attack, defense, speed, height, weight, image, types} = foundPokemon;
    console.log(foundPokemon)
    return(
        <div>
            <div>
                <h2>Buscador</h2>
                <form className="form-container" onSubmit={(e) => handleSubmit(e)}>
                    <div>
                        <input
                        type="text"
                        id="search"
                        name="input"
                        autoComplete="off"
                        value={state.input}
                        onChange={(e) => handleChange(e)}
                        />
                    </div>
                    <br/>
                    <button type="submit">SEARCH POKEMON</button>
                </form>
                <hr/>
            </div>
            <div>
                <Pokemon
                    id = {id}
                    name = {name}
                    image = {image}
                    type1 = {types[0]}
                    type2 = {types[1]}
                />
            </div>
        </div>
    )
}
