import {
    GET_ALL_POKEMONS,
    GET_POKEMON_BY_ID,
    GET_POKEMON_BY_NAME,
    // FILTER_POKEMONS,
    // ORDER_POKEMONS,
    // CREATE_POKEMON,
    // ADD_FAVORITE,
    // REMOVE_FAVORITE, 
    } from "../actions/index"


const initialState = {
    loadedPokemons: [],
    filteredPokemons: [],
    orderedPokemons: [],
    // favoritesPokemons: [],
    pokemonDetail:{}        //esto debería sacarlo de la store
};

const reducer = (state= initialState, action)=>{
    switch(action.type){
        case GET_ALL_POKEMONS:
            return {
                ...state,
                loadedPokemons:action.payload
            }
        case GET_POKEMON_BY_ID:
            return {
                ...state,
                pokemonDetail:action.payload
            }    
        case GET_POKEMON_BY_NAME:
            return {
                ...state,
                pokemonDetail:action.payload
            }



        default:
            return state;
    }
}

export default reducer;
