import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { getPokemonByName } from '../store/actions';


export default function Search (){  
    const [state, setState] = useState({
        input: ""
      });

    const handleChange = (e) => {
        setState({...state,[e.target.name]: e.target.value });
        };

    const dispatch = useDispatch();

    const handleSubmit = (e) =>{
        e.preventdefault();
        dispatch(getPokemonByName(state.inpunt))
        setState({...state,[e.target.name]: '' });
    }
    const foundPokemon = useState((state)=> state.pokemonDetail)

    return(
        <div>
            <h2>Buscador</h2>
            <form className="form-container" onSubmit={(e) => handleSubmit(e)}>
                <div>
                    <input
                    type="text"
                    id="search"
                    name="input"
                    autoComplete="off"
                    value={state.input}
                    onChange={(e) => handleChange(e)}
                    />
                </div>
                <br/>
                <button type="submit">SEARCH POKEMON</button>
            </form>
            <hr/>
        </div>
    );
}
