import axios from "axios"
export const GET_ALL_POKEMONS = 'GET ALL POKEMONS';
export const GET_POKEMON_DETAIL = 'GET POKEMONS DETAIL';
export const GET_POKEMON_BY_NAME = 'GET POKEMON BY NAME';
export const FILTER_POKEMONS = ' FILTER POKEMONS';
export const ORDER_POKEMONS = 'ORDER POKEMONS';
export const CREATE_POKEMON = 'CREATE POKEMON';
export const ADD_FAVORITE = 'ADD FAVORITE';
export const REMOVE_FAVORITE = 'REMOVE FAVORITE';

export function getAllPokemons() {
    return function (dispatch) {
        axios.get("http://localhost:3001/pokemons")
        .then((response) => {
        dispatch({ type: GET_ALL_POKEMONS, payload: response.data });
        })
        .catch((err) => {
            console.log(err)
        })
    };
};

export function getDetailPokemon(id) {
    return function (dispatch) {
        axios.get(`http://localhost:3001/pokemons/${id}`)
        .then((response) => {
        dispatch({ type: GET_POKEMON_DETAIL, payload: response.data });
        })
        .catch((err) => {
            console.log(err)
        })
    };
};

export function getPokemonByName(name){
    return function (dispatch) {
        axios.get(`http://localhost:3001/pokemons?name=${name}`)
        .then((response) => {
        dispatch({ type: GET_POKEMON_BY_NAME, payload: response.data });
        })
        .catch((err) => {
            console.log(err)
        })
    }; 
}
