import React, { useState } from "react";



export default function Create(){
    const [input, setInput] = useState({
        name:'',
        hp:0,
        attack:0,
        defense:0,
        speed:0,
        height:0,
        weight:0,
        image:'',
        type1:'',
        type2:''
    })

    const handleInputChange=(e)=>{
        setInput({
            ...input, [e.target.name]:[e.target.value]
        })
        console.log(input)

    }

    return(
        <div>

            <div>
                <form>
                    <label htmlFor='name'>Name:</label>
                    <input
                        type='text'
                        name='name'
                        value={input.name}
                        onChange={handleInputChange}
                    />
                    <div>
                        <h4>Stats</h4>
                        <label>Health Points:</label>
                        <input
                            type= 'number'
                            name='hp'
                            value={input.hp}
                            onChange={handleInputChange}
                        />
                        <label>Attack:</label>
                        <input
                            type= 'number'
                            name='attack'
                            value={input.attack}
                            onChange={handleInputChange}
                        />
                        <br/>
                        <label>Defense:</label>
                        <input
                            type= 'number'
                            name='defense'
                            value={input.defense}
                            onChange={handleInputChange}
                        />
                        <label>Speed</label>
                        <input
                            type= 'number'
                            name='speed'
                            value={input.speed}
                            onChange={handleInputChange}
                        />
                    </div>
                    <br/>
                    <label>Height:</label>
                    <input
                        type= 'number'
                        name='height'
                        value={input.height}
                        onChange={handleInputChange}
                    />
                    <label>Weight:</label>
                    <input
                        type= 'number'
                        name='weight'
                        value={input.weight}
                        onChange={handleInputChange}
                    />
                    <br/>
                    <label>Image:</label>
                    <input
                        type= 'url'
                        name='image'
                        value={input.image}
                        onChange={handleInputChange}
                    />
                    <br/>
                    <h4>Types</h4>
                    <label htmlFor="type1">Choose a primary tipe:  </label>
                    <select 
                        name="type1"
                        value={input.type1}
                        onChange={handleInputChange}
                    >
                        <option value='electric'>Electric</option>
                        <option value='fire'>Fire</option>
                        <option value='grass'>Grass</option>
                        <option value='poison'>Poison</option>
                    </select> 
                    <br/>
                    <label htmlFor="type2">Choose a secondary type:  </label>
                    <select 
                        name="type2"
                        value={input.type2}
                        onChange={handleInputChange}
                    >
                        <option value='electric'>Electric</option>
                        <option value='fire'>Fire</option>
                        <option value='grass'>Grass</option>
                        <option value='poison'>Poison</option>
                    </select>
                    <br/>
                    <br/>
                    <input type="submit" /> 

                </form>

            </div>

        </div>



    )

}

